//- Preloader
$(window).load(function(){$(".loader").fadeOut(),$(".page-loader").fadeOut("slow")}),$(function(){/* ------------ *
   * Module fade *
  /* ---------- */
var $window=$(window);$(".module-fade").each(function(){var self=$(this);$window.scroll(function(){var topScroll=$(document).scrollTop();topScroll<=self.height()&&self.css("opacity",1-topScroll/self.height()*1.1)})})});